package com.manheim.ws.blobmanager;

import java.io.Serializable;

/**
 *
 * @author rcooper
 */
public class BlobException extends Exception implements Serializable{
    
    /**
     * uid auto generated 
     */
    private static final long serialVersionUID = 1437338214691207509L;
    
    /** Creates a new instance of BlobException */
    public BlobException(String message ) {
        super( message );
    }
    public BlobException(Throwable t) {
        super( t );
    }
}
